import { Component, OnInit } from '@angular/core';
import { AnimeService } from '../anime.service';

@Component({
  selector: 'app-anime-grid',
  templateUrl: './anime-grid.component.html',
  styleUrls: ['./anime-grid.component.css']
})
export class AnimeGridComponent implements OnInit {
  animeList: any = [];

  constructor(private animeService: AnimeService) {}

  ngOnInit(): void {
    this.animeService.getTopAnime()
      .subscribe(anime => this.animeList = anime);
  }
}
